function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),r=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),r.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),r.classList.add("locked"))}));document.querySelectorAll(".menu__item").forEach((t=>{t.addEventListener("click",(function(){s.classList.remove("active"),e.classList.remove("active-burger"),r.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),r.classList.remove("locked"))}))}function newsSlider(){if(!document.querySelector(".news-slider"))return null;new Swiper(".news-slider__swiper",{slidesPerView:2,spaceBetween:20,loop:!0,navigation:{nextEl:".news-slider__arrow--next",prevEl:".news-slider__arrow--prev"},breakpoints:{320:{slidesPerView:2,spaceBetween:20},1025:{slidesPerView:3,spaceBetween:30}}})}burgerMenu(),document.addEventListener("DOMContentLoaded",(function(){newsSlider()}));